import React, { Component, createRef } from "react";
import { func } from "prop-types";

class GoogleMap extends Component {
  constructor(props) {
    super(props);
    this.state = {};
    this.googleMapRef = createRef();
  }

  componentDidMount() {
    const googleMapScript = document.createElement("script");
    googleMapScript.src = `https://maps.googleapis.com/maps/api/js?key=${"AIzaSyCpSyVOMNaAH_omRZGK_iRn1u_91Ot0Rcw"}&libraries=places`;
    window.document.body.appendChild(googleMapScript);

    googleMapScript.addEventListener("load", () => {
      this.googleMap = this.createGoogleMap();
      this.marker = this.createMarker();
    });
  }

  createGoogleMap = () =>
    new window.google.maps.Map(this.googleMapRef.current, {
      zoom: 16,
      center: {
        lat: 43.642567,
        lng: -79.387054
      }
    });

  createMarker = () => {
    var lat, long;
    window.navigator.geolocation.getCurrentPosition(pos => {
      console.log("Retrieved Location");
      //   this.setState({ lat: pos.coords.latitude, long: pos.coords.longitude });
      console.log(this.googleMapRef.current);
      new window.google.maps.Marker({
        position: { lat: pos.coords.latitude, lng: pos.coords.longitude },
        map: this.googleMap
      });
    });
  };

  render() {
    return (
      <div
        id="google-map"
        ref={this.googleMapRef}
        style={{ width: "400px", height: "300px" }}
      />
    );
  }
}

export default GoogleMap;
