import React, { Component, createRef } from "react";
import { func } from "prop-types";
import {
  Navbar,
  Nav,
  Form,
  Row,
  Col,
  Popover,
  OverlayTrigger
} from "react-bootstrap";
import img2 from "./heart.png";
import img from "./question.png";

const popover = (
  <Popover id="popover-basic">
    <Popover.Title as="h3">Help</Popover.Title>
    <Popover.Content>
      Listed towards your left are some symptoms/signs .You can select one or
      more as applicable.
    </Popover.Content>
  </Popover>
);

const Example = () => (
  <OverlayTrigger trigger="click" placement="left" overlay={popover}>
    {/* <Button variant="success" style={{ marginTop: "15vh", marginLeft: "4vw" }}>
      What am I supposed to do now ?
    </Button> */}
    <img
      src={img}
      style={{
        height: "35px",
        width: "35px",
        position: "absolute",
        right: "3vw",
        top: "35vh"
      }}
    />
  </OverlayTrigger>
);

class GoogleMap extends Component {
  constructor(props) {
    super(props);
    this.state = {};
    this.googleMapRef = createRef();
  }

  myHospitals = () => {
    console.log("Calling", this.state.hospitals);
    if (this.state.hospitals) {
      this.state.hospitals.map(hospital => {
        return (
          <div
            style={{
              width: "25px",
              height: "25px",
              backgroundColor: "black",
              position: "absolute",
              top: "75vh",
              left: "20vw"
            }}
            key={`default-radio`}
            // className="mb-3"
          >
            {/* <Form.Check
              type={"radio"}
              id={`default-radio`}
              label={`{hospital.name}`}
            /> */}
            Hello World
          </div>
        );
      });
    } else {
      return "";
    }
  };

  componentDidMount() {
    const googleMapScript = document.createElement("script");
    googleMapScript.src = `https://maps.googleapis.com/maps/api/js?key=${"AIzaSyCpSyVOMNaAH_omRZGK_iRn1u_91Ot0Rcw"}&libraries=places`;
    window.document.body.appendChild(googleMapScript);

    googleMapScript.addEventListener("load", () => {
      this.googleMap = this.createGoogleMap();
      // console.log(this.googleMap);
      this.marker = this.createMarker();
    });
  }

  createGoogleMap = () => {
    console.log("1");
    var map = new window.google.maps.Map(this.googleMapRef.current, {
      zoom: 14,
      center: {
        lat: 19.0968,
        lng: 72.8517
      }
    });
    return map;
  };

  createMarker = () => {
    // var myLatLng = { lat: 43.642567, lng: -79.387054 };
    // var marker = new window.google.maps.Marker({
    //   position: myLatLng,
    //   map: this.googleMap
    // });
    var myLatLng = 0;
    window.navigator.geolocation.getCurrentPosition(pos => {
      myLatLng = { lat: pos.coords.latitude, lng: pos.coords.longitude };

      // console.log("2");
      // console.log(this.googleMap);
      // console.log(pos.coords.latitude, pos.coords.longitude);
      var marker = new window.google.maps.Marker({
        position: myLatLng,
        map: this.googleMap,
        title: "You are here"
      });
      this.googleMap.setCenter({
        lat: pos.coords.latitude,
        lng: pos.coords.longitude
      });
      var pyrmont = new window.google.maps.LatLng(myLatLng.lat, myLatLng.long);
      var request = {
        location: pyrmont,
        radius: "3000",
        query: "hospital"
      };

      var service = new window.google.maps.places.PlacesService(this.googleMap);
      service.textSearch(request, this.callback);
    });
  };

  callback = (results, status) => {
    this.setState({ hospitals: results.slice(0, 5) });
    console.log(this.state);
    if (status == window.google.maps.places.PlacesServiceStatus.OK) {
      for (var i = 0; i < 5; i++) {
        var place = results[i];
        var marker2 = new window.google.maps.Marker({
          position: place.geometry.location,
          map: this.googleMap,
          title: place.name
        });
      }
    }
  };

  render() {
    let hospitals;
    if (this.state.hospitals) {
      hospitals = this.state.hospitals.map(hospital => {
        return (
          <div
            style={{
              width: "25px",
              height: "25px",
              backgroundColor: "black",
              position: "absolute",
              top: "75vh",
              left: "20vw"
            }}
            className="mb-3"
          >
            <Form.Check
              type={"radio"}
              id={`default-radio`}
              label={`{hospital.name}`}
            />
          </div>
        );
      });
    } else {
      hospitals = <p>Waiting</p>;
    }

    return (
      <React.Fragment>
        <Navbar bg="dark" variant="dark">
          <Navbar.Brand href="#home">
            <img
              alt=""
              src="/logo.svg"
              width="30"
              height="30"
              className="d-inline-block align-top"
            />
            {"One Life"}
          </Navbar.Brand>
          <Nav.Link href="#home">
            <img
              src={img2}
              style={{ height: "40px", width: "40px", textAlign: "right" }}
            />
          </Nav.Link>
        </Navbar>
        <div
          id="google-map"
          ref={this.googleMapRef}
          style={{
            width: "50vw",
            height: "50vh",
            marginTop: "5vh",
            marginLeft: "3vw"
          }}
        />
        <Example />
        <Form style={{ position: "absolute", right: "5vw", top: "15vh" }}>
          <Row>
            <Col>
              <Form.Control
                style={{ width: "18vw" }}
                placeholder="First name"
              />
            </Col>
            <Col>
              <Form.Control style={{ width: "18vw" }} placeholder="Last name" />
            </Col>
          </Row>
          <Row>
            <Col>
              <Form.Control
                style={{ width: "18vw", marginTop: "4vh" }}
                placeholder="Phone Number"
              />
            </Col>
            <Col>
              <Form.Control
                style={{ width: "18vw", marginTop: "4vh" }}
                placeholder="Age"
              />
            </Col>
          </Row>
          <Row style={{ marginTop: "3vh", width: "7vw" }}>
            <Col>
              <Form.Group controlId="formBasicCheckbox">
                <Form.Check type="checkbox" label="Bleeding" />
              </Form.Group>
            </Col>
          </Row>
          <Row>
            <Col>
              <Form.Group controlId="formBasicCheckbox">
                <Form.Check
                  type="checkbox"
                  label="Severe Bleeding/Concussion"
                />
              </Form.Group>
            </Col>
          </Row>
          <Row style={{ width: "7vw" }}>
            <Col>
              <Form.Group controlId="formBasicCheckbox">
                <Form.Check type="checkbox" label="Unconscious" />
              </Form.Group>
            </Col>
          </Row>
          <Row style={{ width: "10vw" }}>
            <Col>
              <Form.Group controlId="formBasicCheckbox">
                <Form.Check type="checkbox" label="Animal Bite" />
              </Form.Group>
            </Col>
          </Row>
          <Row style={{ width: "10vw" }}>
            <Col>
              <Form.Group controlId="formBasicCheckbox">
                <Form.Check type="checkbox" label="Heart Attack" />
              </Form.Group>
            </Col>
          </Row>
        </Form>
        {hospitals}
      </React.Fragment>
    );
  }
}

export default GoogleMap;
